import "DPI-C" function void vaddr_write(int addr, byte mask, int data);
module awrite (
    input  [31:0] addr,
    input  [ 3:0] mask,
    input  [31:0] data
);
  always @(*) begin
    vaddr_write(addr, mask, data); // 调用DPI-C写函数
  end
endmodule

import "DPI-C" function int vaddr_read(int addr);
module read (
    input  [31:0] addr,
    output [31:0] data
);
  assign data = vaddr_read(addr); // 调用DPI-C读函数
endmodule
